class DataTraining

	attr_accessor :array, :char, :string

	#Détection des occurences contenant des chiffres

	def figures_detection (array)
		with_figures  = Array.new
		without_figures  = Array.new

		array.each do |handle|
			if handle.delete("0-9") != handle
				with_figures << handle
			else  
				without_figures << handle
			end
		end

		return(with_figures.length)

#		end
	end

#Détection des occurrences contenant une string donnée

	def string_detection (array, string)
		with_string  = Array.new
		without_string  = Array.new

		string=string.upcase

		array.each do |handle|
			if handle.upcase.index(string) != nil
				with_string << handle
			else  
				without_string << handle
			end
		end

		return (with_string.length)

#		puts "#{with_string.length} éléments de cet array contiennent la string '#{string}'. Voulez-vous les afficher (y/n) ?"
#		print "> "
#		if gets.chomp == "y"
#			puts with_string
#		end
	end

#Détection majuscules en début de pseudo(ne gère pas les handles avec _ en handle[1])

	def capitalize_detection (array)
		with_capitalize = Array.new
		without_capitalize = Array.new

		array.each do |handle|
			if handle[1].upcase == handle[1]
				with_capitalize << handle
			else
				without_capitalize << handle
			end
		end

		return with_capitalize.length
#		puts "#{with_uppercase.length} de ces éléments commencent par une majuscule. Voulez-vous les afficher (y/n) ?"
#		print "> "
#		if gets.chomp == "y"
#			puts with_uppercase
#		end
	end

#Détection majuscules (ne gère pas les handles avec _ en handle[1])

	def uppercase_detection (array)
		with_uppercase = Array.new
		without_uppercase = Array.new

		array.each do |handle|
			if handle[1].upcase == handle[1]
				with_uppercase << handle
			else
				without_uppercase << handle
			end
		end

		return with_uppercase.length
#		puts "#{with_uppercase.length} de ces éléments commencent par une majuscule. Voulez-vous les afficher (y/n) ?"
#		print "> "
#		if gets.chomp == "y"
#			puts with_uppercase
#		end
	end

	def char_count (array,char)
		with_char = Array.new
		without_char = Array.new
		count = 0

		array.each do |handle|
			if handle.count char > 0
				count += handle.count char
				with_char << handle
			else
				without_char << handle
			end
		end

		return count

#		puts "Cet array contient #{count} fois #{char}. Voulez-vous afficher les handle (y/n) ?"
#		print "> "
#		if gets.chomp == "y"
#			puts with_char
#		end
	end

	def perform (array,string,char)

		puts "Cette liste contient #{array.length} handles, dont :"
		puts "- #{figures_detection(array)} contenant des chiffres."
#		puts "- #{string_detection (array, string))} contenant '#{@string}'."
		puts "- #{capitalize_detection (array)} commençant par une majuscule."
#		puts "- #{char_count (array,char)} '#{char}', tous handle confondus."
	end
end

